import React from 'react';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';

function App() {

  function handleProfileButtonClick() {
    // const userInfoObj = userInfo.getUserInfo();
    // nameInput.value = userInfoObj.name;
    // jobInput.value = userInfoObj.about;
    const popup = document.querySelector('.popup_type_edit');
    popup.classList.add('popup_opened')
  };

  return (
    <div className="body">
      <div className="page">
      <Header />
      <main className="content">
            <div className="profile">
                <div className="profile__avatar-wrapper">
                    <img src={profileAvatar} alt="Жак-Ив Кусто" class="profile__ellipse"/>
                    <button class="profile__ellipse-pencil" type="button"></button>
                </div>
                <div className="profile__info">
                    <h1 className="profile__info-name">Жак-Ив Кусто</h1>
                    <button type="button" className="profile__info-edit-button">
                        <img src={pencil} alt="Карандаш" class="profile__pencil" onclick={handleProfileButtonClick}/>
                    </button>
                    <p className="profile__info-text">Исследователь океана</p>
                </div>
                <button type="button" className="profile__add-button"></button>
            </div>
            <section className="elements">
            </section>
        </main>
      <Footer />
      <div className="popup popup_type_edit">
      <div className="popup__container">
        <button className="popup__close-button" type="button"></button>
        <h2 className="popup__title">Редактировать профиль</h2>
        <form className="popup__form" name="popupEditForm" novalidate>
          <input className="popup__input popup__input_type_name" id="profile-name" type="text" name="name" placeholder="Имя" minlength="2" maxlength="40" required/>
          <span className="profile-name-error popup__error"></span>
          <input className="popup__input popup__input_type_job" id="profile-job" type="text" name="description" placeholder="Вид деятельности" minlength="2" maxlength="200" required/>
          <span className="profile-job-error popup__error"></span>
          <button className="popup__save-button" type="submit">Сохранить</button>
        </form>
      </div>
    </div>
      </div>
    </div>
  );
}

export default App;
