import React from 'react';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';

function App() {
  const [isEditProfilePopupOpen, setEditProfilePopupOpen] = React.useState(false)
  const [isAddPlacePopupOpen, setAddPlacePopupOpen] = React.useState(false)
  const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = React.useState(false)

  function handleEditProfileClick() {
    setEditProfilePopupOpen(true)
}

  function handleAddPlaceClick() {
    setAddPlacePopupOpen(true)
  }

  function handleAvatarProfileClick() {
    setEditAvatarPopupOpen(true)
  }

  return (
    <div className="body">
      <div className="page">
      <Header />
      <Main 
        onEditProfile={handleEditProfileClick}
        onAddPlace={handleAddPlaceClick}
        onEditAvatar={handleAvatarProfileClick}
      />
      <Footer />
      <PopupWithForm 
        isOpen={isEditProfilePopupOpen ? "popup_opened" : ""}
        name="popupEditForm"
        title="Редактировать профиль"
        buttonText="Сохранить"
      >
        <input 
        className="popup__input popup__input_type_name" 
        id="profile-name" 
        type="text" 
        name="name" 
        placeholder="Имя" 
        minLength="2" 
        maxLength="40" 
        required
        />
        <span className="profile-name-error popup__error"></span>
          <input 
          className="popup__input popup__input_type_job" 
          id="profile-job" 
          type="text" 
          name="description" 
          placeholder="Вид деятельности" 
          minLength="2"
          maxLength="200" 
          required
          />
          <span className="profile-job-error popup__error"></span>
        </PopupWithForm>
      {/* <PopupWithForm /> */}
      {/* <PopupWithForm /> */}
      <ImagePopup/>
      <div className="popup popup_type_add">
        <div className="popup__container">
          <button className="popup__close-button" type="button"></button>
          <h2 className="popup__title">Новое место</h2>
          <form className="popup__form" name="cardForm" noValidate>
            <input className="popup__input popup__input_type_name" id="cards-name" type="text" name="name" placeholder="Название" minLength="2" maxLength="30" required/>
            <span className="cards-name-error popup__error"></span>
            <input className="popup__input popup__input_type_job" id="cards-link" type="url" name="link" placeholder="Ссылка на картинку" required/>
            <span className="cards-link-error popup__error"></span>
            <button className="popup__save-button" type="submit">Создать</button>
          </form>
        </div>
      </div>
      <div className="popup popup_type_avatar">
        <div className="popup__container">
          <button className="popup__close-button" type="button"></button>
          <h2 className="popup__title">Обновить аватар</h2>
          <form className="popup__form">
            <input className="popup__input popup__input_type_avatar" id="popup-avatar" type="url" name="userAvatar" placeholder="Ссылка на картинку" required/>
            <span className="popup__error popup-avatar-error"></span>
            <button className="popup__save-button" type="submit">Сохранить</button>
          </form>
        </div>
      </div>
      </div>
    </div>
  );
}

export default App;
